import React, { useEffect, useState } from 'react'

import Dado from '../../components/Dado';
import Missao from '../../components/Missao';

import DadoModel from '../../models/Dado';
import MissaoModel from '../../models/Missao';
import JogadaModel from '../../models/Jogada';

function Home(props) {

    const [ jogada, setJogada ] = useState(new JogadaModel());
    const [ missao, setMissao ] = useState(new MissaoModel());

    useEffect(() => {
        geraMissao();
    }, [] )

    function jogar() {
        let j = jogada;
        alert(JSON.stringify(j));
        if(j.getTurno() == 0) {
            j.preencheDados();
        }
        j.jogar();
        j.setTurno(j.getTurno()+1);
        setJogada(j);
        alert(jogada);
    }

    function geraMissao() {
        let m = new MissaoModel();
        /*m.setUm(4);
        m.setDois(6);
        m.setTreis(9);
        m.setQuatro(10);
        m.setCinco(17);
        m.setSeis(12);
        m.setSeguida(0);
        m.setTrinca(1);
        m.setQuadra(0);
        m.setFlush(1);
        m.setGeneral(0);*/

        setMissao(m);
    }

    return (<>
        <div className="col-xl-12 mt-3 mb-5 d-flex flex-row">
            <button
                onClick={jogar}
                className="btn btn-success mb-3 mr-5"
            >
                Jogar
            </button>
            {!!jogada.getDados().length && ( <>
                {jogada.getDados().map(d => (
                    <div className="ml-2">
                        <Dado dado={d.getNumero()} />
                    </div>
                ))}
                {
                    jogada.getTurno()
                }        
            </>)}
        </div>
        <div className="col-xl-12 mt-5">
                <Missao missao={missao} />
        </div>
    </>)
}

export default Home;
