import OpcaoJogadaUm from '../OpcoesJogada/OpcaoJogadaUm';

class OpcaoJogadaFactory {

    createOpcao(opcao) {
        let op = null;
        if(opcao == "um") {
            op = new OpcaoJogadaUm();
        }
        return op;
    }
}

export default OpcaoJogadaFactory;
