import React, { useEffect, useState } from 'react'

import Dado from '../../components/Dado';
import Missao from '../../components/Missao';

import DadoModel from '../../models/Dado';
import MissaoModel from '../../models/Missao';
import JogadaModel from '../../models/Jogada';

function Home(props) {

    const [ dados, setDados ] = useState([]);
    const [ missao, setMissao ] = useState(new MissaoModel());

    useEffect(() => {
        jogar();
        geraMissao();
    }, [] )

    function jogar() {

        let jogada = new JogadaModel();
        for(let i=0; i<5; i++) {
            jogada.setDados([]);
            jogada.getDados().push(new DadoModel());
        }

        jogada.jogar();

        setDados(jogada.getDados());
    }

    function geraMissao() {
        let m = new MissaoModel();
        /*m.setUm(4);
        m.setDois(6);
        m.setTreis(9);
        m.setQuatro(10);
        m.setCinco(17);
        m.setSeis(12);
        m.setSeguida(0);
        m.setTrinca(1);
        m.setQuadra(0);
        m.setFlush(1);
        m.setGeneral(0);*/

        setMissao(m);
    }

    return (<>
        <div className="col-xl-12 mt-3 mb-5 d-flex flex-row">
            <button
                onClick={jogar}
                className="btn btn-success mb-3 mr-5"
            >
                Jogar
            </button>
            {dados.length && ( <>
                {dados.map(d => (
                    <div className="ml-2">
                        <Dado dado={d.getNumero()} />
                    </div>
                ))}
            </>)}
        </div>
        <div className="col-xl-12 mt-5">
                <Missao missao={missao} />
        </div>
    </>)
}

export default Home;
