import Player from '../Player';
import Turno from '../Turno';

class Partida4P {
    id;
    tipo;
    players;
    turno;

    constructor() {
        this.tipo = "4P";
        this.players = [
            () => {
                for(let i=0; i<8; i++) {
                    return new Player();
                }
            }
        ];
        this.players[0].setCor("Azul");
        this.players[1].setCor("Vermelho");
        this.players[2].setCor("Preto");
        this.players[3].setCor("Verde");
        this.players[4].setCor("Branco");
        this.players[5].setCor("Rosa");
        this.players[6].setCor("Roxo");
        this.players[7].setCor("Amarelo");
        this.turno = new Turno();
        this.turno.setPlayers(this.players);
        this.turno.inicioRandom(this.players);
        this.turno.total[0] = 84;
    }

    setId(id) {
        this.id = id;
    }
    getId() { 
        return this.id;
    }

    getTipo() {
        return this.tipo;
    }

    setPlayers(players) {
        this.players = players;
    }
    getPlayers() {
        return this.players;
    }

    setTurno(turno) {
        this.turno = turno;
    }
    getTurno() {
        return this.turno;
    }
}

export default Partida4P;
