import React, { useEffect, useState } from 'react'

import Dado from '../../components/Dado';
import Missao from '../../components/Missao';

import DadoModel from '../../models/Dado';
import MissaoModel from '../../models/Missao';
import JogadaModel from '../../models/Jogada';

function Home(props) {

    const [ jogada, setJogada ] = useState(new JogadaModel());
    const [ missao, setMissao ] = useState(new MissaoModel());

    useEffect(() => {
        geraMissao();
    }, [] )

    function jogar() {
        let j = new JogadaModel();
        j.setDados(jogada.getDados());
        j.setTurno(jogada.getTurno());
        if(j.getTurno() == 0) {
            j.preencheDados();
        }
        j.jogar();
        setJogada(j);
    }

    function salvarDado(id) {
        alert(id)
    }

    function geraMissao() {
        let m = new MissaoModel();
        /*m.setUm(4);
        m.setDois(6);
        m.setTreis(9);
        m.setQuatro(10);
        m.setCinco(17);
        m.setSeis(12);
        m.setSeguida(0);
        m.setTrinca(1);
        m.setQuadra(0);
        m.setFlush(1);
        m.setGeneral(0);*/

        setMissao(m);
    }

    return (<>
        <div className="col-xl-12 mt-3 mb-5 d-flex flex-row">
            <button
                onClick={jogar}
                className="btn btn-success mb-3 mr-5"
            >
                Jogar
            </button>
            {!!jogada.getDados().length && ( <>
                {jogada.getDados().map(d => ( <>
                    <span className="ml-2" onClick={salvarDado}>
                    </span>
                    <Dado dado={d} />
                </>))}
                {
                    jogada.getTurno()
                }        
            </>)}
        </div>
        <div className="col-xl-12 mt-5">
            <Missao missao={missao} />
        </div>
    </>)
}

export default Home;
