import React, { useEffect, useState } from 'react'
import Missao from '../../components/Missao';
import Jogada from '../../components/Jogada';
import MissaoModel from '../../models/Missao';
import PartidaFactory from '../../models/Partidas/PartidaFactory';

function Partida(props) {

    const [ missao, setMissao ] = useState(new MissaoModel());
    const [ partida, setPartida ] = useState(new PartidaFactory().createPartida("default"))

    useEffect(() => {
    }, intervaloAtt() )

    function intervaloAtt() {
        setInterval(() => { document.getElementById('att-missao').click() }, 3000)
    }

    function attMissao() {
        let m = new MissaoModel();

        let array = [
            missao.getOpcoes()[0],
            missao.getOpcoes()[1],
            missao.getOpcoes()[2],
            missao.getOpcoes()[3],
            missao.getOpcoes()[4],
            missao.getOpcoes()[5],
            missao.getOpcoes()[6],
            missao.getOpcoes()[7],
            missao.getOpcoes()[8],
            missao.getOpcoes()[9],
            missao.getOpcoes()[10],
        ];
        m.setOpcoes(array);
        m.calculaPontuacao();
        setMissao(m);
    }

    return (<>
        <div className="row"> 
            <div className="col-lg-9 d-flex flex-column mt-2">
                <Jogada 
                    missao={partida.turno.atual.getMissao()} 
                    setMissao={setMissao} 
                    partida={partida} 
                    setPartida={setPartida}
                />
            </div>
            <div className="col-lg-3 mt-2">
                <button className="btn btn-success mb-2" id="att-missao" onClick={attMissao}><i className="fa fa-spinner"></i></button>
                <Missao missao={partida.turno.atual.getMissao()} />
            </div>
    </div>
    </>)
}

export default Partida;
