import React, { useEffect, useState } from 'react'
import Missao from '../../components/Missao';
import Jogada from '../../components/Jogada';
import MissaoModel from '../../models/Missao';

function Home(props) {

    const [ missao, setMissao ] = useState(new MissaoModel());

    useEffect(() => {
        geraMissao();
    }, [] )

    setInterval(setMissao(missao), 1000);

    function geraMissao() {
        let m = new MissaoModel();
        /*m.setUm(4);
        m.setDois(6);
        m.setTreis(9);
        m.setQuatro(10);
        m.setCinco(17);
        m.setSeis(12);
        m.setSeguida(0);
        m.setTrinca(1);
        m.setQuadra(0);
        m.setFlush(1);
        m.setGeneral(0);*/

        setMissao(m);
    }

    function attMissao() {
        let m = new MissaoModel();
        m.setUm(missao.getUm());
        m.setDois(missao.getDois());
        m.setTreis(missao.getTreis());
        m.setQuatro(missao.getQuatro());
        m.setCinco(missao.getCinco());
        m.setSeis(missao.getSeis());
        setMissao(m);
    }

    return (<>
        <div className="row"> 
            <div className="col-lg-9 d-flex flex-column mt-2">
                <Jogada missao={missao} setMissao={setMissao} />
            </div>
            <div className="col-lg-3 mt-2">
                <button className="btn btn-success mb-2" onClick={attMissao}><i className="fa fa-spinner"></i></button>
                <Missao missao={missao} />
            </div>
    </div>
    </>)
}

export default Home;
