import React, { useEffect, useState } from 'react';
import { Link, Redirect, Switch } from 'react-router-dom';
import Partida from '../../components/Partida';
import PartidaFactory from '../../models/Partidas/PartidaFactory';

function IniciarPartida(props) {

    const [ tipo, setTipo ] = useState("");
    const [ custom, setCustom ] = useState(0);

    return (<>
        {!tipo && (<>
            <div className="d-flex flex-column m-5">
                <button onClick={() => setTipo("2P")} className="btn-success">2P</button>
                <button onClick={() => setTipo("4P")} className="btn-success">4P</button>
                <button onClick={() => setTipo("8P")} className="btn-success">8P</button>
            </div>
        </>)}
        {tipo && (<>
            <Switch>
                <Partida partida={new PartidaFactory().createPartida(tipo)} />
            </Switch>
        </>)}
    </>)
}

export default IniciarPartida;
