import Dado from '../models/Dado';

class Jogada {

    dados;
    salvos;
    turno;

    constructor() {
        this.dados = [];
        this.turno = 0;
    }

    setDados(dados) {
        this.dados = dados;
    }
    getDados() {
        return this.dados;
    }

    setSalvos(salvos) {
        this.salvos = salvos;
    }
    getSalvos() {
        return this.salvos;
    }

    setTurno(turno) {
        this.turno = turno;
    }
    getTurno() {
        return this.turno;
    }

    jogar() {
        if(this.turno >= 3) {
            return null;
        }
        this.dados.map(j => {
            do {
                j.jogarDado();
            } while(j.getNumero() <= 0);
        })
        this.turno = this.turno + 1;
    }

    preencheDados() {
        for(let i=0; i<5; i++) {
            this.dados.push(new Dado());
        }
    }
}

export default Jogada;
