import React, { useEffect, useState } from 'react'

import Dado from '../../components/Dado';
import Missao from '../../components/Missao';

import DadoModel from '../../models/Dado';
import MissaoModel from '../../models/Missao';

function Home(props) {

    const [ dados, setDados ] = useState([]);
    const [ missao, setMissao ] = useState(new MissaoModel());

    useEffect(() => {
        jogar();
        geraMissao();
    }, [] )

    function jogar() {
        let d = new DadoModel();
        do {
            d.jogarDado();
        } while(d.getNumero() <= 0);
        setDado(d);
    }

    function geraMissao() {
        let m = new MissaoModel();
        /*m.setUm(4);
        m.setDois(6);
        m.setTreis(9);
        m.setQuatro(10);
        m.setCinco(17);
        m.setSeis(12);
        m.setSeguida(0);
        m.setTrinca(1);
        m.setQuadra(0);
        m.setFlush(1);
        m.setGeneral(0);*/

        setMissao(m);
    }

    return (<>
        <div className="col-xl-12 mt-3">
            <button
                onClick={jogar}
                className="btn btn-success mb-3"
            >
                Jogar
            </button>
            {dados.length && ( <>
                {dados.map(d => (
                    <Dado dado={d.getNumero()} />
                ))}
            </>)}
        </div>
        <div className="col-xl-12 mt-3">
            <Missao missao={missao} />
        </div>
    </>)
}

export default Home;
