import React, { useEffect, useState } from 'react'

import Dado from '../../components/Dado';
import Missao from '../../components/Missao';
import Jogada from '../../components/Jogada';

import DadoModel from '../../models/Dado';
import MissaoModel from '../../models/Missao';

function Home(props) {

    const [ dados, setDados ] = useState([]);
    const [ missao, setMissao ] = useState(new MissaoModel());

    useEffect(() => {
        jogar();
        geraMissao();
    }, [] )

    function jogar() {
        let d1 = new DadoModel();
        let d2 = new DadoModel();
        let d3 = new DadoModel();
        let d4 = new DadoModel();
        let d5 = new DadoModel();

        let jogada = [d1,d2,d3,d4,d5];

        jogada.map(j => {
            do {
                j.jogarDado();
            } while(j.getNumero() <= 0);
        })

        setDados(jogada);
    }

    function geraMissao() {
        let m = new MissaoModel();
        /*m.setUm(4);
        m.setDois(6);
        m.setTreis(9);
        m.setQuatro(10);
        m.setCinco(17);
        m.setSeis(12);
        m.setSeguida(0);
        m.setTrinca(1);
        m.setQuadra(0);
        m.setFlush(1);
        m.setGeneral(0);*/

        setMissao(m);
    }

    return (<>
        <div className="col-xl-12 mt-3 mb-5 d-flex flex-row">
            <button
                onClick={jogar}
                className="btn btn-success mb-3 mr-5"
            >
                Jogar
            </button>
            {dados.length && ( <>
                {dados.map(d => (
                    <div className="ml-2">
                        <Dado dado={d.getNumero()} />
                    </div>
                ))}
            </>)}
        </div>
        <div className="col-xl-12 mt-5">
                <Missao missao={missao} />
        </div>
    </>)
}

export default Home;
