class OpcaoJogadaSeguida {
    tipo;
    dados;
    total;
    controle;

    constructor() {
        this.tipo = "seguida";
        this.dados = [];
        this.total = 0;
        this.controle = 0;
    }

    getTipo() {
        return this.tipo;
    }

    setDados(dados) {
        this.dados = dados;
    }
    getDados() {
        return this.dados;
    }

    setTotal(total) {
        this.total = total;
    }
    getTotal() {
        return this.total;
    }

    setControle(controle) {
        this.controle = controle;
    }
    getControle() {
        return this.controle;
    }

    bubbleSort(array) {
        let len = array.length;
        for(let i=0; i<len; i++) {
            for(let j=0; j<len-1; j++) {
                if(array[j] > array[j+1]) {
                    let aux = array[j];
                    array[j] = array[j+1];
                    array[j+1] = aux;
                }
            }   
        }
        return array;
    }

    b(array) {
        let len = array.length;
        let checked;
        do {
            checked = false;
            for (let i = 0; i < len; i++) {
                if (array[i] > array[i + 1]) {
                    let tmp = array[i];
                    array[i] = array[i + 1];
                    array[i + 1] = tmp;
                    checked = true;
                }
            }
        } while (checked);
        return array;
    }

    filtro(array) {
        this.dados = this.bubbleSort(array);
        this.total = 1;
        this.controle++;
    }
}

export default OpcaoJogadaSeguida;
