import Dado from '../models/Dado';

class Jogada {

    dados;
    turno;

    setDados(dados) {
        this.dados = dados;
    }
    getDados() {
        return this.dados;
    }

    setTurno(turno) {
        this.turno = turno;
    }
    getTurno() {
        return this.turno;
    }

    jogar() {
        this.dados.map(j => {
            do {
                j.jogarDado();
            } while(j.getNumero() <= 0);
        })
    }

    preencheDados() {
        this.dados = [];
        for(let i=0; i<5; i++) {
            this.dados.push(new Dado());
        }
    }
}

export default Jogada;
